using Server;using Server.Items;using Server.Network;using System;using System.Collections;namespace Server.Items{	public class BodyPartsContainer : BaseContainer	{		public BodyPartsContainer( ) : base( 13718 )		{			Movable = false;			Visible = false;		}				public override bool Decays		{			get			{				return true;			}		}		public override bool IsAccessibleTo( Mobile m )		{			return false;		}				public override void OnDoubleClick( Mobile from )		{			return;		}				public override void OnDoubleClickSecureTrade( Mobile from )		{			return;		}				public BodyPartsContainer( Serial serial ) : base( serial )		{		}		public override void Serialize( GenericWriter writer )		{			base.Serialize( writer );			writer.Write( (int) 0 );		}		public override void Deserialize( GenericReader reader )		{			base.Deserialize( reader );			int version = reader.ReadInt();		}	}}